    private void deliverablesComboActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deliverablesComboActionPerformed
        this.contentPanel.removeAll();
        switch (deliverablesCombo.getSelectedIndex()) {
            case 0:
                this.contentPanel.add(beveragePanel1);
                break;
            case 1:
                this.contentPanel.add(decorationPanel2);
                break;
            default:
                throw new RuntimeException("Unable to display this unknown Panel");
        }
        this.contentPanel.revalidate();
        this.contentPanel.repaint();

    }//GEN-LAST:event_deliverablesComboActionPerformed
